import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import { FaHome, FaBuilding, FaPlus, FaBars } from 'react-icons/fa';



const Sidebar = () => {
  const [collapsed, setCollapsed] = useState(false);

  const menuItems = [
    { path: '/', label: 'Home', icon: <FaHome /> },
    { path: '/apartments', label: 'Apartments', icon: <FaBuilding /> },
    { path: '/create', label: 'Create Apartment', icon: <FaPlus /> },
  ];

  return (
    <aside
      style={{
        width: collapsed ? '60px' : '200px',
        transition: 'width 0.3s',
        padding: 'var(--spacing)',
        backgroundColor: 'var(--secondary-color, #1e40af)',
        color: '#fff',
        height: '100vh',
        position: 'sticky',
        top: 0,
      }}
    >
      {/* Collapse / Expand Button */}
      <div
        style={{
          display: 'flex',
          justifyContent: collapsed ? 'center' : 'flex-end',
          marginBottom: '20px',
        }}
      >
        <button
          onClick={() => setCollapsed(!collapsed)}
          style={{
            background: 'none',
            border: 'none',
            color: '#fff',
            fontSize: '20px',
            cursor: 'pointer',
          }}
        >
          <FaBars />
        </button>
      </div>

      {/* Menu */}
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {menuItems.map((item) => (
          <li key={item.path} style={{ marginBottom: '16px' }}>
            <NavLink
              to={item.path}
              style={({ isActive }) => ({
                display: 'flex',
                alignItems: 'center',
                color: '#fff',
                padding: '8px',
                borderRadius: '8px',
                backgroundColor: isActive ? '#2563eb' : 'transparent',
                textDecoration: 'none',
              })}
            >
              <span style={{ fontSize: '18px', marginRight: collapsed ? 0 : '10px' }}>
                {item.icon}
              </span>
              {!collapsed && <span>{item.label}</span>}
            </NavLink>
          </li>
        ))}
      </ul>
    </aside>
  );
};

export default Sidebar;
