import React from "react";
import "../index.css"; // अपने project की main CSS

export default function ApartmentDetail({ apartment }) {
  // Props में apartment object आएगा:
  // { title, location, price, imageURL, status, description }

  if (!apartment) {
    return <p>Loading...</p>;
  }

  return (
    <div className="apartment-detail-container">
      <button
        className="back-btn"
        onClick={() => window.history.back()}
      >
        &larr; Back
      </button>

      <div className="apartment-detail-card">
        {/* Image */}
        <img
          src={apartment.imageURL}
          alt={apartment.title}
          className="apartment-detail-image"
        />

        {/* Content */}
        <div className="apartment-detail-content">
          <h2 className="apartment-title">{apartment.title}</h2>
          <p className="apartment-location">{apartment.location}</p>
          <p className="apartment-price">${apartment.price}</p>

          {/* Status Badge */}
          <span
            className={`status-badge ${
              apartment.status === "available"
                ? "status-available"
                : "status-rented"
            }`}
          >
            {apartment.status === "available"
              ? "Available"
              : "Rented"}
          </span>

          {/* Description */}
          <p className="apartment-description">
            {apartment.description ||
              "No description available for this apartment."}
          </p>
        </div>
      </div>
    </div>
  );
}
