import { useEffect, useState } from "react";

export default function Bookmarks() {
  const [bookmarked, setBookmarked] = useState([]);

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("apartments")) || [];
    const filtered = stored.filter((ap) => ap.isBookmarked);
    setBookmarked(filtered);
  }, []);

  return (
    <div className="Apartment-list-container">
      <h2>Your Bookmarked Apartments</h2>
      {bookmarked.length === 0 ? (
        <p>No bookmarks added yet.</p>
      ) : (
        bookmarked.map((Apartment) => (
          <div key={Apartment.id} className="Apartment-box">
            <img src={Apartment.images[0]} alt={Apartment.title} />
            <div className="properties-description">
              <h3>{Apartment.title}</h3>
              <p>{Apartment.location}</p>
              <p>Price: â‚¬{Apartment.price}</p>
            </div>
          </div>
        ))
      )}
    </div>
  );
}
